<div class="container p-3">
    <div class="row justify-content-center">
        <div class="col-md-6">
            <div class="card">
                <header class="card-header">
                    <a href="signin.php" class="float-right btn btn-outline-primary mt-1">Ingresar</a>
                    <h4 class="card-title mt-2">Registrarse</h4>
                </header>
                <article class="card-body">
                    <form id="signup_form" action="../api/signup.php">
                        <div class="form-row">
                            <div class="col form-group">
                                <label>Nombre </label>   
                                <input type="text" name="nombre" class="form-control" placeholder="Nombre">
                            </div>
                        </div>
                        <div class="form-group">
                                <label class="form-check form-check-inline">
                            <input class="form-check-input" type="radio" name="rol" value="administrador">
                            <span class="form-check-label"> Administrador </span>
                            </label>
                            <label class="form-check form-check-inline">
                            <input class="form-check-input" type="radio" name="rol" value="usuario">
                            <span class="form-check-label"> Usuario</span>
                            </label>
                        </div>
                        <div class="form-group">
                            <label>Generar contrase√±a</label>
                            <input class="form-control" name="contrasena" type="password">
                        </div>  
                        <div class="form-group">
                            <button type="submit" class="btn btn-primary btn-block"> Registrarse  </button>
                        </div>    
                    </form>
                </article> 
            </div>
        </div>
    </div>
</div> 

